void mainImage( out vec4 O, in vec2 f )
{
    vec2 uv = f / iResolution.x;
    float t = iTime * 0.1;
    float yy = exp(uv.y * 6.),
        k = floor(yy)*2.0,
        x = fract(uv.x*k*3.0);
    vec2 u = vec2(x, fract(yy));
    u.y = abs(u.y-0.5);
    float b = u.x + u.y*2.0,
        a = uv.y * .1,
        d = smoothstep(b + a , b - a, 1.);
    vec3 blue = vec3(0., 120., 191.) / 255.;
    O = vec4(mix(blue, vec3(1.), d),1.);
}
