void draw(inout vec3 c, float mask, vec3 p)
{
    c = mix(c, p, clamp(mask, 0., 1.));
}

float circle(vec2 uv, float r)
{
 	return step(length(uv), r);
}

float circleOutline(vec2 uv, float r1, float r2)
{
    return circle(uv, r1) - circle(uv, r2);
}

float box(vec2 uv, vec2 s)
{
    //return step(abs(uv.x), s.x) * step(abs(uv.y), s.y);

    s = step(abs(uv), s);
    return s.x * s.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 blue = vec3(0.15, 0.22, 0.26) * 1.2;
    vec3 red = vec3(0.9, 0.3, 0.33);

    vec3 c = blue;

    float moon = circle(uv + vec2(0.0, -0.1), 0.1);

    moon *= sin(-uv.x * 5. + uv.y * 2. + 6.8);

    float shine = 1. - circle(uv + vec2(-0.26, -0.05), 0.3);
    float stick = box(uv + vec2(0., -0.1), vec2(0.003, 0.2));
    float ball = circle(uv, 0.023);
    float tri = box(vec2(uv.x / (uv.y + 0.07) / 8., uv.y + 0.1), vec2(0.03));
    float tri2 = box(vec2(uv.x / (uv.y - 0.008) / 0.5, uv.y + 0.33), vec2(0.2));
    float ladder = box(vec2(uv.x - uv.y * 0.1, mod(uv.y + 0.2, 0.04)), vec2(0.02, 0.003));
    ladder *= step(uv.y, -0.15);
    float onion = circleOutline(vec2(uv.x / (uv.y * uv.y * 20. - 1.1), uv.y - 0.18), 0.055, 0.05);
    onion += circleOutline(vec2(uv.x / (uv.y * uv.y * 20. - 1.), uv.y - 0.18), 0.055, 0.05);
    onion += circleOutline(vec2(uv.x / (uv.y * uv.y * 20. - 0.8), uv.y - 0.18), 0.055, 0.05);
    float rooster = circle(vec2(uv.x - 0.01 + sin(-uv.y * 100. - 1.) * 0.005, uv.y - 0.31 + sin(uv.x * 150. + 0.5) * 0.02), 0.025);

    draw(c, moon, red);
    draw(c, moon * shine, vec3(0.95));
    draw(c, stick + ball, vec3(0.));
    draw(c, tri, vec3(0.28));
    draw(c, tri2 + ladder, vec3(0.));
    draw(c, onion, vec3(0.));
    draw(c, rooster, vec3(0.));

    c *= 1. - length(uv) * 0.6; // vignette
    c = max(vec3(0.1), c); // fade
    c += sin(sin(uv.x * 123. + uv.y * 987.) * uv.x * 99999.) * 0.02; // noise

    fragColor = vec4(c, 1.);
}
