#define f(U)   ( (U).x > 0. ? .5 : 0. )   // this should always equals .5

void mainImage( out vec4 O, vec2 u)
{
    vec2 R = iResolution.xy,
         U = 2.* u / R - 1.;     // normalized coordinates  
    O = vec4(0);

    if (U.y > .33)                                          // top
       U = fract(U),   O.r = U.x + f(U);

    else if (U.y > -.33)                                    // middle : should be like top
       O.g = ( U = fract(U) ).x  + f(U);

    else                                                    // bottom : should be offseted compare to top 
       O.b =  U.x  + f( U = fract(U) );

    O = step(.7,O);
}
