void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    uv += vec2(0, 1);
    uv *= .5;
    float t = iTime/3.+4.7;

    float g = uv.y+.025;

    vec3 orange = pow(vec3(.65, .4, .15), vec3(.1/g) );
    vec3 blue   = pow(vec3(.15, .4, .65), vec3(5.*g) );
    vec3 ozone  = mix(vec3(1), vec3(.25, .13, .5), g);

    uv += vec2(cos(t), sin(t)+1.1);
    float light = 5.*exp(-2.*length(uv) );

    vec3 col = orange*blue*ozone*light;

    fragColor = vec4(pow(col, vec3(.45) ), 1);
}
