// ======= Internal Code =================

float getGrid(float gridSize, vec2 uv)
{
    if(uv.x < gridSize && uv.x > -gridSize)
        return 1.0;

    if(uv.y < gridSize && uv.y > -gridSize)
        return 1.0;

    if(fract(uv.x) < gridSize)
        return 0.5;

    if(fract(uv.y) < gridSize)
        return 0.5;

    return 0.0;
}

float sampleF(vec2 uv)
{
    float res = f(uv.x, iTime);
    float dist = abs(uv.y - res);
    return 1.0 - smoothstep(0.0, lineWidth, dist);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord;
    uv -= iResolution.xy * 0.5;
    uv *= gridScale;
    uv -= gridOffset;

    vec3 col = vec3(51, 171, 255) / 255.0;

    col = mix(col, vec3(36, 106, 155) / 255.0, getGrid(0.025, uv));

    float c = sampleF(uv);

    col = mix(col, vec3(1), c); 

    fragColor = vec4(col,1.0);

    //fragColor = vec4(uv, 0, 1.0);
}

// ============= User Code ================

vec2 gridOffset = vec2(0, 0);
vec2 gridScale = vec2(0.02);
float lineWidth = 0.04;

float f(float x, float t)
{
    return sin((x + t) * 2.0) + x * 0.5 * sin(t/5.0);
}
