vec3 circle (vec2 pos, float radius, vec2 uv, float ratio)
{
 	vec2 newPos = vec2(pos.x * ratio, pos.y);
    vec3 col = vec3(step(radius,distance(uv,newPos)));

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
    uv.x *=ratio;

    //1 - Position of my circle
    vec2 pos = vec2(.2 , .5);
    vec2 pos2 = vec2(.8 ,0.5);


    //2 distance() = sqrt(x^2 + y^2)
    //Distance Field
    float beat = sin(sin(iTime * .9) + 1.);
    float radius = beat;

    //vec3 col = vec3(step(radius,distance(uv,pos)));

    float dist = distance(uv,pos) * distance(uv,pos2);

    vec3 colorA = vec3(10.,10.,0.);
    vec3 colorB = vec3(10.,0.,0.);
    vec3 circle1 = circle(pos,0.2,uv, ratio);

    vec3 circle2 = circle(pos2,0.2,uv,ratio);

	colorA += vec3(circle1);
    colorB += vec3(circle2);

    vec3 col = (colorA * colorB);

    // Output to screen
    fragColor = vec4(col,0.);
}
