void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
         // = vec2(iResolution.x/iResolution.y, 1.) - 2.0 * fragCoord /iResolution.y;
    	 // = vec2(1., 1.) - 2.0 * fragCoord /iResolution.xy;
    vec3 pos = vec3(xy, sqrt(1. - dot(xy, xy)));
          // = vec3(xy, sqrt(1. - pow(length(xy), 2.0)));
    vec3 lightPos = normalize(vec3(cos(iTime), sin(iTime), 1.));
    float l = dot(lightPos, pos);
    fragColor = l * vec4(1., 0., 0., 1.);
}
