float mapRange01(float x, float min, float max) 
{
    if (x>1.) return max;
    else if (x<0.) return min;
    else return min+(max-min)*x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy*.5) /iResolution.y;

    uv *= 16.;

    if (mod(floor(uv.x),2.) == 0.)
        uv.x = 1. - uv.x;

    if (mod(floor(uv.y),2.) == 0.)
        uv.y = 1. - uv.y;

    float b = abs( 1.- fract(uv.x)-fract(uv.y ));

//logo design
    float paramMin = fract(iTime * .5 + (fragCoord.x + fragCoord.y)/2000.) * 2.;
    float paramMax = paramMin;
    if (paramMax > 1.)
        paramMax = 2.-paramMax;

    float minBorder = mapRange01(paramMax, .90, .3);
    float maxBorder = mapRange01(paramMin, .80, .1);

    if (b>minBorder) b=0.;
    else if (b<maxBorder) b=0.;
    else b=1.;

//grid
    if (fract(uv.x)>.98 || fract(uv.x)<.02) b=0.;
    if (fract(uv.y)>.98 || fract(uv.y)<.02) b=0.;

    vec3 col = vec3(b,b,b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
