#define PI 3.14159265359
#define TWO_PI 6.28318530718


float Circle(vec2 uv, vec2 p,float r, float blur) {

    float d = length(uv-p);    
    float c = smoothstep(r, r - blur,  d);
    return c;    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    vec2 p = vec2(.0, .0);
    float c = Circle(uv, p, .5, .009);
    c -= Circle(uv, vec2(sin(.0 + iTime) * (.5 - .1), cos(.0 + iTime) * (0.5 - .1)), .1, .009);
    c -= Circle(uv, vec2(sin(.0 + iTime * PI) * (.21), cos(.0 + iTime * PI) * (.21)), .1, .009);
    c -= Circle(uv, vec2(sin(.0 + iTime * TWO_PI) * (.02), cos(.0 + iTime * TWO_PI) * (.02)), .1, .009);


    // Output to screen
    fragColor = vec4(vec3(c, 0.0, 0.0),  1.0);
}
