mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 m = iMouse.xy/iResolution.xy;
    float zoom = pow(.1,clamp(0.,0.5,abs(sin(iTime*0.5))));
    vec3 col = vec3(0.);

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float h = 2. +sin(iTime);
    vec2 c = uv*zoom*2.*rotate2d(h*0.1*3.14);


    vec2 z = vec2(0.);
    float iter = 0.;

    const float max_iter=100.;
    float ma=100.;


    for (float i=0.; i<max_iter;  i++){

        z = vec2(z.x*z.x-z.y*z.y,2.*z.x*z.y)+c;

        ma=min(ma,abs(z.y)*abs(z.y));

        if(length(z)>2.) break;
        iter++;
    }

    float f = iter/max_iter*h;
    f=pow(f,.025);
    f=ma*h;

    col = mix(texture(iChannel0,vec2(f,1.)).gbr,texture(iChannel1,vec2(f,.8)).rgb,texture(iChannel2,vec2(f,.125)).rgb);
    /*
    col *=texture(iChannel0,vec2(f,.5)).gbb;
    col =texture(iChannel1,vec2(f,.25)).ggg;


    */
    col +=texture(iChannel2,vec2(f,clamp(0.5,1.,abs(tan(iTime*0.08))))).gbr;

    fragColor = vec4(col,1.0);
}
