float getB(float r0, vec3 p){
	 // 反演球面
    vec3 o = vec3(0.0, r0, 0.0);
    float r1 = 1.41421356 * r0;
    //
    vec3 op = p - o;
    vec3 op1 = op * (2.0 * r0 * r0 / dot(op, op));//op * (r1 * r1 / dot(op, op));
    vec3 p1 = op1 + o;
    return p1.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
	col = col * 0.0;
    // Output to screen

    float r0 = 0.3;

    vec3 p = vec3(uv.x - 0.5, uv.y - 0.5 + r0, 0.0);

   	float B = getB(r0, p);

    float r = mod(B * 10.0, 1.0);

    r = abs((r - 0.5) * 2.0);


    col.r = r;

    fragColor = vec4(col,1.0);
}
