const float small_radius = 0.08;
const float big_radius = 0.35;
const int disc_count = 12;

float disc(vec2 uv, vec2 center, float radius)
{
	vec2 delta = (uv-center)/radius;
    float sqDistance = dot(delta, delta);
    return smoothstep(1.01, 0.99, (sqDistance));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.yy;

    vec3 col = vec3(0.5); //0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    for(int i=0; i<disc_count; ++i)
    {
        if ( int(iTime*6.)%disc_count == i )
        {
            continue;
        }
        float angle = -6.28 * float(i) / float(disc_count);
        vec2 center = big_radius * vec2( cos(angle), sin(angle) );
    	col = mix(col, vec3(1,0,1), disc( uv, center, small_radius));
    }
    col *= 1.-disc(uv, vec2(0.), 0.01);

    // Output to screen
    fragColor = vec4(col,1.0);
}
