#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)

#define X 4.
vec2 h(vec2 p)//Dave H
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
float time;
float ln (vec2 p, vec2 a, vec2 d) {
    float r = clamp(dot(p-a,d)/dot(d,d),-1.,1.);
    return length(p-a-d*r);
}
void mainImage( out vec4 Q, vec2 U )
{
    vec4 a = A(U);
    Q =  vec4(1);
    for (int x = -2; x <= 2; x++)
    for (int y = -2; y <= 2; y++) {
        vec2 V = floor(0.5+(U-0.5*R)/X+vec2(x,y))*X+0.5*R;
        V += X*h(V)-.5;
        vec4 a = A(V);
        time = iTime+h(V+3.).x;
        Q *= 1.-.4*exp(-ln(U,V,X*normalize(a.xy)));
    }


}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
void mainImage( out vec4 Q, vec2 U )
{
    Q = A(U);
   	vec4
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    Q = mix(Q,0.25*(n+e+s+w),.5);
    Q.z = Q.z+0.25*(n.y-s.y+e.x-w.x);
    Q.xy =  mix(Q.xy,0.25*vec2(e.z-w.z,n.z-s.z),.5);
    if (length(Q.xy)>0.) Q.xy = mix(Q.xy,Q.z*normalize(Q.xy),.1);


    if (length(Q.xy)==0.) Q = 0.*sin(.1*(U.x*vec4(1,2,3,4)))*sin(.1*(U.x+U.y*vec4(1,2,3,4)));
    if (iMouse.z>0.&&length(U-iMouse.xy)<10.) Q.z = 1.;
    if (length(Q.xy)==0.) Q = vec4(sin(.3*U.y),cos(.3*U.x),0,1);
    if (iFrame<1&&length(U-0.5*R)<10.) Q.z = sin(iTime);

}
