vec2 BumpToNormal(in vec2 uv, float strength)
{
    vec2 s = 1.0/iChannelResolution[0].xy;

    float p = texture(iChannel0, uv).x;
    float h1 = texture(iChannel0, uv + s * vec2(1.0,0)).x;
    float v1 = texture(iChannel0, uv + s * vec2(0,1.0)).x;

   	return (p - vec2(h1, v1)) * strength + 0.5;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 color = BumpToNormal(uv, 10.0);

    // Output to screen
    fragColor = vec4(color,1.0,0.0);
}
