float steps = 25.,
     maxRad = 500.;

float get_layer(vec2 uv){
    float timeModifier = ( sin(iTime/5.) + 1.3 ) / 2., i,
          step_k = floor( steps * timeModifier ) / steps * maxRad;
    vec2 k = exp2( sin(step_k*uv) );
    i = 1.- (k.x + k.y) / 4.;    
    return i*i*i;
}

#define rotate(a)  mat2(cos(a), sin(a), -sin(a), cos(a));

void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;
    float a =   sin(iTime/5.) *U.x
              + cos(iTime/5.)
              + texture(iChannel0, vec2(0)).r / 4.;
    U *= rotate(a);

    O =   clamp(texture(iChannel0, U).r*4., 1., 4.) // music
        * vec4( get_layer(U),
                get_layer(U + vec2(.4,0) ),
                get_layer(U + vec2(0,-.4)),
                1 );
}
