bool mask1(vec2 uv){
    float len = sqrt(pow((uv.x - 0.25) * 2.0,2.0) + pow(uv.y - 0.5,2.0));
    if(uv.x < 0.5 && len < 0.5){
    	return true;
    }
    return false;
}

bool mask2(vec2 uv){
    float len = sqrt(pow((uv.x - 0.75) * 2.0,2.0) + pow(uv.y - 0.5,2.0));
    if(len < 0.5){
    	return true;
    }
    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float shift = iTime * 0.2;

    vec2 uv = fragCoord/iResolution.xy;

    if(mask1(uv) || mask2(uv)) {
        vec4 pixelColor = texture(iChannel0, vec2(uv.x + shift, uv.y)); 
        fragColor = vec4(pixelColor);	
    }else{
    	fragColor = vec4(0.0);	
    }
}
