#define field(U) 10.* vec2( f1(U),f2(U))


mat2 rotate2d(float angle)
{
    return mat2(cos(angle),-sin(angle),sin(angle),cos(angle));
}

float f1(vec2 U)
{    
    U = U.y * ( vec2(5,3) + .01*sin(iTime*.05) );
    return sin(U.x) + cos(U.y);
}

float f2(vec2 U)
{
    U = U.x * ( vec2(5,3) + .01*sin(iTime*.05) );
    return sin(U.y) - cos(U.x);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy-0.5;

	uv.x *= iResolution.x/iResolution.y;

    vec4 col,col1, col2;

    uv *= 2.0;

    vec2 fld1 = field(uv);

    col1 = vec4(0.0,0.0,0.0,1.0);

    col1 += sin(vec4(1.0,0.0,0.0,0.1) + fld1.x + fld1.y)*10.0;   

    uv *= 2.1;

    uv = rotate2d(0.005*iTime*3.14)*uv;

    vec2 fld2 = field(uv);

    col2 = vec4(1.0,1.0,0.0,1.0);

    col2 += sin(vec4(1.0,0.0,0.0,0.1) + fld2.x - fld2.y)*10.0;

    col = mix(col1,col2,0.5);

	fragColor = col;
}
