void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy; // 0 to 1
    uv -= 0.5; //move to center of screen
    float d = length(uv);
    float c = d;
    if(d < 0.3) { c = 1.0; } //it's an oval because of the aspect ratio
    else { c = 0.0; }
    fragColor = vec4(vec3(c) ,1.0);
}
