#define BG_COLOR vec3(0.00, 0.05, 0.20)
#define GRID_COLOR vec3(1.00, 0.20, 0.60)
#define GLOW_COLOR vec3(0.20, 1.00, 0.60)
#define GRID_SIZE 0.10
#define GRID_SIZE_HALF GRID_SIZE * 0.5
#define GRID_LINE_SIZE 1.25
#define GLOW_CONSTANT 0.2
#define GLOW_MULTIPLIER 0.3

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 grid = abs(mod(uv + GRID_SIZE_HALF, GRID_SIZE) - GRID_SIZE_HALF) / fwidth(uv);
    float glowFactor = abs(cos(iTime)) * GLOW_MULTIPLIER + GLOW_CONSTANT;
    float line = min(grid.x, grid.y) / GRID_SIZE;
    float glow = min(grid.x, grid.y) / glowFactor;
    vec3 lineColor = mix(BG_COLOR, GRID_COLOR, smoothstep(GRID_LINE_SIZE / GRID_SIZE, 0.0, line));
    vec3 glowColor = mix(BG_COLOR, GLOW_COLOR, smoothstep(GRID_LINE_SIZE / GRID_SIZE, 0.0, glow));
    vec3 color = mix(lineColor, glowColor, uv.x * (sin(iTime) + 1.0) / 1.5);

    fragColor = vec4(color, 1.0);
}
