void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord / iResolution.xy;
    float pix = 1.5/iResolution.y; 
    //float a = abs(sin(uv.x * 10.));
	float a = uv.x ;

	//vec3 col = vec3( ( smoothstep( a, a + pix, uv.y ) ) ) ;
    //vec3 col = vec3( ( smoothstep( a - pix, a, uv.y ) ) - ( smoothstep( a, a + pix, uv.y ) ) ) ;
    vec3 col = vec3( ( smoothstep( pix, 0., abs( ( uv.y - a) ) ) ) ) ;
    //vec3 col = vec3 ( abs( ( uv.y - a) ) );
    //vec3 col = vec3( ( step( a, uv.y ) ) - step( a + pix, uv.y ) ) ;

    fragColor = vec4(col,1.0);
}
