//please check https://en.wikipedia.org/wiki/Kernel_(image_processing)#Convolution for kernel
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy*sin(iTime);
    vec2 uv = fragCoord/iResolution.xy ;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    vec2 res = iResolution.xy*sin(iTime*0.1);
    vec4 a0 = texture(iChannel0, (fragCoord+vec2(-1.,-1.))/res);
    vec4 a1 = texture(iChannel0, (fragCoord+vec2(0.,-1.))/res);
    vec4 a2 = texture(iChannel0, (fragCoord+vec2(1.,-1.))/res);
    vec4 a3 = texture(iChannel0, (fragCoord+vec2(-1,0.))/res);
    vec4 a4 = texture(iChannel0, (fragCoord+vec2(0,0.))/res);
    vec4 a5 = texture(iChannel0, (fragCoord+vec2(1.,0.))/res);
    vec4 a6 = texture(iChannel0, (fragCoord+vec2(-1.,1.))/res);
    vec4 a7 = texture(iChannel0, (fragCoord+vec2(0.,1.))/res);
    vec4 a8 = texture(iChannel0, (fragCoord+vec2(1.,1.))/res);

    vec3 col = vec3(0.);
    col += a0.rgb*1.;
    col += a1.rgb*0.;
    col += a2.rgb*-1.;
    col += a3.rgb*0.;
    col += a4.rgb*0.;
    col += a5.rgb*0.;
    col += a6.rgb*1.;
    col += a7.rgb*0.;
    col += a8.rgb*-1.;
    col = col/1.;
    //t = vec4(uv, 0, 1.);
    // Output to screen
    fragColor = vec4(col.r, col.r, col.r, 1.0);
}
