void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y - vec2(0.5,0.5);

    // Calculate torus
    const float speed = 3.0;
    const float freq  = 30.0;

    float wave_pattern = sin(iTime*(-speed)+length(uv)*freq);
    float light = length(freq*tan(iTime)*uv-1.5);
    float gray=wave_pattern/light;  //remove wave_pattern/ from here to see light by itself

    vec3 col = vec3(gray,gray,gray*.5);

    // Output to screen
    fragColor = vec4(col,1.0);
}
