void mainImage( out vec4 C, vec2 F )
{
    vec2 p = iResolution.xy;
    p = abs((F + F - p) / p.y);
    p = 12. * vec2(length(p), atan(p.y, p.x));  
    p = p.x * vec2(cos(p.y),sin(p.y));     
    C = vec4(4./abs(p),p.x/p.y,1);
}

