#define horizontal_repetition 2.5
#define vertical_repetition 2.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec4 textureColor = texture( iChannel0, uv );

    float horizontalUV = mod( uv.x * iResolution.x, horizontal_repetition * 2.0);
    if( horizontalUV > horizontal_repetition )
        horizontalUV = horizontal_repetition - mod(horizontalUV, horizontal_repetition);

    float verticalUV = mod( uv.y * iResolution.y, vertical_repetition * 2.0);
    if( verticalUV > vertical_repetition )
        verticalUV = vertical_repetition - mod(verticalUV, vertical_repetition);

    float blendedCoordinate =( horizontalUV / horizontal_repetition) + (verticalUV / vertical_repetition );

    fragColor = vec4( textureColor * blendedCoordinate);
}
