vec2 brickTile(vec2 st, float zoom)
{
    // 拡大
    st *= zoom;
    // 座標と原点のラジアン
    float radian = atan(st.y,st.x);
    //縦横どちらを動かすか　
    float isMoveLine = step(0.499999,(fract(iTime)));
    // modとfloorを奇数行と偶数行を0と1に
    // isMoveLineで停止する時間を作る
    vec2 f = (floor(mod(st,2.))* 2. -1. ) * iTime;
    st.x +=  f.y * isMoveLine * 2.;
    st.y +=  f.x * (mod(isMoveLine + 1.,2.)) * 2.;
    return fract(st);
} 


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    uv = brickTile(uv,5.);

   vec3 col = vec3(smoothstep(0.29,0.3, length(uv - 0.5)));

    // Output to screen
    fragColor = vec4(col,1.0);
}
