// variant of "free local contrast " https://shadertoy.com/view/Mt2cDm

#define T(l) textureLod( iChannel0, U/R, l )

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    O = .5 + 30.*(T(0.) - T(1.));  // highest frequency
  //O = .7* T(0.) / T(1.);         // local contrast
    O.a = 0.;

    vec4 T = T(3.);
    float m = min(T.r,min(T.g,T.b)),
          M = max(T.r,max(T.g,T.b));

    O = dot(O,O) * (T-m)/(M-m);
        // highest frequency luminance * lowres chrominance
}
