void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;  
    float t = iTime * .2;
    float v = uv.y;
    v += t;
    float e = v;
    vec2 g = fract(uv*10.0)*iResolution.x/-iResolution.y;
    float r = length(g-0.5);
    float d =step(r,0.1);
   uv.y+=uv.x*0.25;
    //e += sin(uv.x * 10.0 + t) * .01;
    float f = pow((uv.x - .5) * 1.2, 2.) * 2.;
    e += sin(f * 2.0 + t) * .4;
    float z = t+99.9;
    z += (sin(t*.5)+2.3)*0.17;
    uv.x += z;
    vec3 a = vec3(1.1,0.99,.95);
    vec3 b = vec3(.0,.0,.1);
    e -= fract((uv.x * uv.y * 0.017+t) * 3.0 - 2.2) * 1.2;
    float m = e;
    float c = smoothstep(m,m + 1.5 / iResolution.y, .5);
  c = (sin(m*1.6 )+.5)*0.45 + 0.34;
  //c *= sin(uv.y*2.3);
    fragColor.rgb = d+mix(a,b,c);
}
