float time_offset = 0.1; // second

float x_offset = 0.005;
float speed = 0.3;

float line_x = 0.3;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    if (uv.x < line_x && uv.x > line_x-max(0.,iTime*speed-time_offset))
        fragColor = texture(iChannel1, vec2(uv.x + x_offset, uv.y));

    else if (uv.x > 1.-line_x && uv.x < 1.-line_x+max(0.,iTime*speed-time_offset))
        fragColor = texture(iChannel1, vec2(uv.x - x_offset, uv.y));

    else
        fragColor = texture(iChannel0, uv);        

}
