mat3 kernel = mat3(
    0.0, -1., 0.0,
    -1., 4.0, -1.,
    0.0, -1., 0.0
);

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 color = vec4(0.0);
    vec2 texel = 1.0 / iResolution.xy;

    for (int y = 0; y < 3; y++)
    for (int x = 0; x < 3; x++)
        color += texture(iChannel0, uv + texel * vec2(x - 1, y - 1)) * kernel[x][y];

    color *= 3.0;

    fragColor = color * vec4(3.0) + texture(iChannel0, uv);
}
