float Circle(vec2 uv, vec2 offset, float r, float blur)
{
    float d=length(uv-offset);

    d = smoothstep( r, r - blur, d );

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    const float S = 5.0;
    float c = 0.0;
    for( float i = -S; i <= S; ++i )
    {
        c += Circle( uv, vec2(0.15*i, 0.1), 0.05, 0.01 );
        c -= Circle( uv, vec2(0.15*i - 0.005*(S - i), 0.1), 0.05, 0.01 );

    }
    //c += Circle( uv, vec2(0.0, 0.1), 0.05, 0.01 );

    fragColor = vec4(vec3(c) ,1.0);
}

