#define LOOP_COUNT_I 64
#define LOOP_COUNT_F 64.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = -1.0 + 2.0*fragCoord/iResolution.xy;
    vec2 m = -1.0 + 2.0*iMouse.xy/iResolution.xy;

    vec3  col = vec3(0.0);
    vec2  d = (m-p)/LOOP_COUNT_F;
    float w = 1.0;
    vec2  s = p;
    for( int i=0; i<LOOP_COUNT_I; i++ )
    {
        col += w*smoothstep( 0.0, 1.0, texture(iChannel0, s * 0.3).xyz);
        w *= 0.99;
        s += d;
    }
    col = col * 2.0 / LOOP_COUNT_F;

	fragColor = vec4(col, 0.0);
}
