void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = ( fragCoord - .5 * iResolution.xy ) / iResolution.y;

    vec3 col = vec3 (0.);

    float pix = 1.5 / iResolution.y;

    //vec2 st = vec2(atan(uv.x, uv.y)*4., length(uv));
    //uv = vec2(st.x/6.2831, st.y);

    uv.x += iTime * .2;

    vec3 d = vec3( sin( ( uv.y - uv.x ) *30. ) );
    col = smoothstep(pix, 0., d);

    col *= 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    fragColor = vec4(col,1.0);
}
