void mainImage( out vec4 C, vec2 S ){
     vec2 u;
     C -=C;
     u.x = 2.-4.* S.x/iResolution.x;
     u.y = 2.-2.* S.y/iResolution.y;

   for(int i = 0; i <4; ++i) {
     u += sin(atan(u.y,u.x)*4.+
          vec2(0.,(4.7+.1*sin(2.*iTime))))*
          pow(length(u),-3.);
   }
   C +=   exp(dot(u,u)*-.05) ;     
}
