void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    vec2 center = iResolution.xy / iResolution.x * 0.5;
    float amplitude = 100.0;

    // Fade to black and white
    float p = cos(iTime - distance(uv, center));
    p -= sin(iTime * 0.5 - distance(uv, center));

    // Left waves
    p += sin(iTime - distance(uv, vec2(center.x - 0.25, center.y + 0.25)) * amplitude);
    p += sin(iTime - distance(uv, vec2(center - 0.25)) * amplitude);

    // Right waves
    p += sin(iTime - distance(uv, vec2(center + 0.25)) * amplitude);
    p += sin(iTime - distance(uv, vec2(center.x + 0.25, center.y - 0.25)) * amplitude);

    // Output to screen
    fragColor = vec4(vec3(p), 1.0);
}
