float smax(float a, float b, float k){
    float h = clamp((b-a)/k+.5,0.,1.);
	return mix(a,b,h) + h*(1.-h)*k*.5;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2    uv  =  fragCoord/iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    vec3 col = vec3(1.);

    vec2 m = iMouse.xy/iResolution.xy; //normlize mouse coords too

    uv.x*=.75;
    uv.y+=.1;

    //make white circle in center of screen
    vec2 circle_center = vec2(0.);
    float radius = .3;
    float blur = m.y*radius;
    uv.y -= smax(blur,pow(abs(uv.x*.2),.4),.2)-blur*.5;

    float d = length(uv-circle_center)-.01;
    float circle = 1.-smoothstep(radius+blur,radius-blur,d);

    col *= vec3(circle);
    col *= vec3(1.,.2,.1);

    //shape circle into heart
    // Output to screen
    fragColor = vec4(col,1.0);
}
