#define samp 8.
float removeGreen(vec4 col){
    return step(col.g,col.r+col.b);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mousePos = iMouse.xy/iResolution.xy;

    vec4 tex = texture(iChannel0,uv);
    tex = (tex+0.25*vec4(cos(iTime)) ) * removeGreen(tex);
    vec4 final = tex;

    vec2 lightPos = iMouse.xy/iResolution.xy;
    for ( float i = 0.; i < samp; i += 1.0 ) {
        vec4 dt = texture(iChannel0, (uv-lightPos)*texture(iChannel1,fract(fragCoord/8.)).r*(i/samp)+lightPos)*1./samp*2.;
        dt = mix( vec4(0.),dt, removeGreen(dt)); //Light from him

        /* //try this, light behind him
        dt.rgb = vec3(0.1,0.2,0.3)*(1.-removeGreen(dt));
        dt *= smoothstep(0.2,dt.g,dt.r+dt.b);
        */

        final += dt;
    }
    fragColor = final;
}
