void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color

    //vec4 color = texture(iChannel0, uv);
    vec4 texelValue= texelFetch(iChannel0, ivec2(fragCoord.xy), 0);

    // Output to screen
    fragColor = vec4(texelValue.rgb,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 newColor = vec3(0.01,0.03,0.04) + texelFetch(iChannel0, ivec2(fragCoord.xy), 0).rgb;
    vec4 texelValue=vec4(newColor,1.0) ;

    if(texelValue.r > 1.0) {
     fragColor = vec4(vec3(0.0,0.0,0.0),1.0) ;
    }
    else
    {
     fragColor = texelValue;
    }

}
