///------------------------------------

vec3 rotate_y(vec3 v, float angle)
{
	float ca = cos(angle); float sa = sin(angle);
	return v*mat3(
		ca, 0.0, -sa,
		0.0, 1.0, 0.0,
		sa, 0.0, ca);
}

vec3 rotate_x(vec3 v, float angle)
{
	float ca = cos(angle); float sa = sin(angle);
	return v*mat3(
		1.0, 0.0, 0.0,
		0.0, ca, -sa,
		0.0, sa, ca);
}


vec3 rotate_z(vec3 v, float angle)
{
	float ca = cos(angle); 
    float sa = sin(angle);
	return v*mat3(
		ca, -sa, 0.0,
		sa, ca, 0.0,
		0.0, 0.0, 1.0);
}

vec2 rotate(vec2 v, float a) {
   return vec2(cos(a)*v.x + sin(a)*v.y, -sin(a)*v.x + cos(a)*v.y);
}

vec2 rotatev2(vec2 p, float ang)
{
    float c = cos(ang);
    float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}


mat2 rot2D(float a)
{
    float c=cos(a);
    float s=sin(a);
    return mat2(c,-s,s,c);
}


///--------------------------------------
float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {
    vec3 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - h*ba) - r;
}

float sdQuadraticCurve(vec3 p, vec3 a, vec3 b, vec3 c, float r) {
	float d = 17.0;
	vec3 o = a;
	float rr = 0.0;
	float or = r;
	for(int i = 1; i < 7; i++) {
		float h = float(i) / 6.0;
		vec3 q = mix(mix(a, b, h), mix(b, c, h), h);
		vec3 po = p - o;
		vec3 qo = q - o;
		float k = clamp(dot(po, qo) / dot(qo, qo), 0.0 + 0.1, 1.0 - 0.1);
		rr = r + sin(h * 3.141592) * r;
		float dd = length(po - k*qo) - mix(or, rr, k);
		d = min(d, dd);
		o = q;
		or = rr;
	}
	return d;
}

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * ((b - a) / k), 0.0, 1.0);
    return mix(b, a, h) - k*h*(1.0 - h);
}

float smin2(float a, float b, float k, float f) 
{
    float h = clamp(0.5 + 0.5 * ((b - a) / k), 0.0, 1.0);
    return mix(b, a, h) - k*h*(1.0 - h);
}

float peloybrazos(vec3  p)
{
    float d=9999.9;
    //pelo
    d = smin(d, sdQuadraticCurve(p, vec3(-0.1, 0.76, 0.0), vec3(-0.4, 0.5, 0.0), vec3(-0.3, 0.2, 0.0), 0.015), 0.05);
    d = smin(d, sdQuadraticCurve(p, vec3(0.1, 0.76, 0.0), vec3(0.4, 0.5, 0.0), vec3(0.3, 0.2, 0.0), 0.015), 0.05);

    //pelo
    d = smin(d, sdQuadraticCurve(p, vec3(-0.1, 0.6, 0.0), vec3(-0.4, 0.5, 0.2), vec3(-0.5, 0.2, 0.2), 0.015), 0.05);
    d = smin(d, sdQuadraticCurve(p, vec3(0.1, 0.6, 0.0), vec3(0.4, 0.5, 0.2), vec3(0.5, 0.2, 0.2), 0.015), 0.05);

    //brazo


    d = smin(d, sdQuadraticCurve(p, vec3(-0.1, 0.46, 0.1), vec3(-0.1, 0.45, -0.25), vec3(-0.3, 0.42, 0.52), 0.015), 0.05);
    d = smin(d, sdQuadraticCurve(p,  vec3(0.1, 0.46, 0.1),  vec3(0.1, 0.45, -0.25), vec3(0.3, 0.42, 0.52), 0.015), 0.05);
    return d;
}    


///---------------------------------------
float mapSer(vec3 p) 
{
    vec3 q = p;

    float d = 9999.0;
    q = vec3(0.0, 0.4, 0.0);
    d = min(d, sdSphere(p - q, 0.2));
    d = smin(d, sdCapsule(p, q, q + vec3(0.0, 0.3, 0.0), 0.1), 0.1);
    d = max(d, -sdSphere(p - q - vec3(-0.04, 0.35, 0.08), 0.02));
    d = max(d, -sdSphere(p - q - vec3(0.04, 0.35, 0.08), 0.02));
    d = min(d, sdSphere(p - q - vec3(-0.04, 0.35, 0.07), 0.01));
    d = min(d, sdSphere(p - q - vec3(0.04, 0.35, 0.07), 0.01));
    d = smin(d, sdCapsule(p, q + vec3(-0.08, 0.0, 0.0), q + vec3(-0.1, -0.48, 0.0), 0.025), 0.1);
    d = smin2(d, sdCapsule(p, q + vec3(0.08, 0.0, 0.0), q + vec3(0.1, -0.48, 0.0), 0.025), 0.1, 0.9);

    return d;
}

float opScaleSer( in vec3 p, in float s  )
{
    return  mapSer(p/s)*s;
}


float opScaleBrazos( in vec3 p, in float s  )
{
    return  peloybrazos(p/s)*s;
}


