float modulo(float x)
{
    return x - floor(x);
}

const float FieldOfViewMultiplier = 400.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord2D = (fragCoord - iResolution.xy * 0.5) * FieldOfViewMultiplier / iResolution.yy;

    coord2D = pow(vec2(2), coord2D);

    float value = coord2D.x / coord2D.y;
    vec3 c;
    if(isnan(value))
    {
       c = vec3(1,0,0);
    }
    else if(isinf(value))
    {
        c = vec3(1,1,0);
    }
    else if(value == 0.)
    {
        c = vec3(0);
    }
    else
    {
       c = vec3(modulo(log2(coord2D.x)/4.), modulo(log2(coord2D.y)/4.), modulo(log2(value)/4.)); 
    }


    fragColor = vec4(c,1);

}
