void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;//Condensing this into one line

    vec4 texColorVideo1 = texture(iChannel0,xy);//Get the pixel at xy from iChannel0
    vec4 texColor2Video2 = texture(iChannel1,xy);//Get the pixel at xy from iChannel0


  	float greenR = 16.0/255.0;
    float greenB = 34.0/255.0;
    float greenG = 152.0/255.0;

    float gap = 0.1;


    bool currentChannelRIsGreen= (texColorVideo1.r <= (greenR + gap)) && (texColorVideo1.r >= (greenR - gap));
    bool currentChannelBIsGreen= (texColorVideo1.b <= (greenB + gap)) && (texColorVideo1.b >= (greenB - gap));
    bool currentChannelGIsGreen= (texColorVideo1.g <= (greenG + gap)) && (texColorVideo1.g >= (greenG - gap));



    if(currentChannelRIsGreen && currentChannelBIsGreen && currentChannelGIsGreen){
      fragColor = texColor2Video2;
    }else{
	  fragColor = texColorVideo1;
	}


}
