void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;

     vec3 color = vec3(0.0);
	vec2 lb=(st)* 1.0/0.1;
    lb=floor(lb);
    vec2 lt=floor((1.0-st)*10.0);
    vec2 inv_lb=1.0-lb;
    vec2 inv_lt=1.0-lt;
    color=vec3(lb.x*lt.x*lt.y*lb.y)+ //white
        (lt.y)*(lb.y)*vec3(1.0,0.0,0.0)+ //red
        (lt.x*lb.x)*vec3(0.0,1.0,0.0)+ //green
        (inv_lb.x*inv_lb.y*inv_lt.x*inv_lt.y)*vec3(0.0,0.0,1.0);//blue

    fragColor = vec4(color,1.0);
}
