#define PI_4 0.78539815
#define PI_2 1.5707963

#define SIZE iResolution.y*.1
#define SF SIZE/min(iResolution.x,iResolution.y)

#define rand1(p) fract(sin(p* 78.233)* 43758.5453) 


mat2 rot(float a){
	float ca = cos(a);
    float sa = sin(a);
    return mat2(ca,-sa,sa,ca);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

    vec2 guv = uv*SIZE;
    vec2 gid = floor(guv);
    guv = fract(guv) - .5;

    float gidHash = floor((rand1(gid.x + gid.y*100.) + iTime*.1)*2.);    
    guv *= rot(PI_2 * (gidHash));

    float sf = SF*SIZE;

    // float m = 1. - (smoothstep(guv.x-SF, guv.x, guv.y) - smoothstep(guv.x, guv.x+SF, guv.y));        
    float m = 1. - smoothstep(SF,0.,abs(guv.y-guv.x));      

    fragColor = vec4(m);
}
