    g = texture(iChannel0,uv(fragCoord.xy,iResolution.xy,vec2(0.)) ).x/2.;


    if(n(fragCoord.xy, iMouse.xy, 70.) == 1){g =+ 1.;}



    for(int h = 0; h < 4; h++){
        g = pow(g,f);
        g += pow(texture(iChannel0,uv(fragCoord.xy,iResolution.xy,
