void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
    float blend = sin(time) * 0.5 + 0.5;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.x / iResolution.y;

    vec2 position = vec2(0.5);
	vec2 offset = vec2(uv.x, (uv.y * 2.0 - 1.0) / aspect * 0.5 + 0.5) - position;

    float l0 = length(offset);
	float d0 = smoothstep(0.05, 1.0, l0);
    float a0 = atan(offset.y, offset.x);
    float a1 = atan(offset.y + offset.x * sin(l0 * 40.0) * 0.1,
                    offset.x - offset.y * sin(l0 * 30.0) * 0.2);

	float factor = smoothstep(0.0, 1.0, 4.0 *
		(smoothstep(0.0, 1.0, l0) + 0.5) *
        (smoothstep(0.1, 0.2, l0 * blend)) *
        (smoothstep(0.2, 1.0, 1.0 - l0)) *
		(sin(l0 * 40.0 - 3.14 * 0.5 - blend * 15.0) * 0.4 + 0.6 * blend) *
        (sin(a1 * 8.0 - time * 2.0) * 0.5 + 0.5 * blend) * 
        (sin((uv.x * 1.0 + uv.y * 2.0) * 10.0 * blend + 5.0) * 0.4 + 0.5));

    float final = factor;

    fragColor = vec4(vec3(final), 1.0);
}
