vec2 complexSq(vec2 z) {

	return vec2(z.x * z.x - z.y*z.y, 2.*z.x*z.y);
}

#define N_PATHS 3.
#define CYCLE_LEN 65.


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float speed = iMouse.y/iResolution.y;
    float time = mod(iTime * speed * 2., CYCLE_LEN);

    float path = mod(iTime * speed * 2., CYCLE_LEN * N_PATHS) / CYCLE_LEN;

    vec2 c = (fragCoord - vec2(iResolution.x / 2., iResolution.y / 2.)) / iResolution.x ;
    c *= 10.*exp(-time/ 5.) ;

    vec2 z = vec2(0,0);


    if (path < 1.)
      c-= vec2(0.693645, 0.7);
    else if (path < 2.)
      c-= vec2(3./4., 0.0 - exp(-time/ 10.));
    else
        c-= vec2(1.00949, -0.31231);



    float j = 0.;
    while (length(z)< 2.) {
      if (path >= 1.)
        z = complexSq(z) + c;
      else
        z = complexSq(complexSq(complexSq(z))) + c;  // other set
      j += 1.;
      if (j >= 500.)
          break;
    }

    fragColor = vec4(vec3((2.+z.x)/4.,(2.+z.y)/4.,1) * ( j / 20. * (exp(-time/ 15.)  + 0.01 )) ,1);
}
