#define PI 3.1415926
#define TAU 6.2831852

#define BLACK_COL vec3(16,22,26)/255.
#define WHITE_COL vec3(235,241,245)/255.

#define SF 1./min(iResolution.x,iResolution.y)*2.
#define ssin(v) sin(v)*.5+.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;       

    float a = atan(uv.y, uv.x)/TAU;

    float l = length(uv);
    float m = 0.;
    for(float n=0.; n<16.; n+=1.){
        float i = n*PI*.25;

        float tt = 2.;
        float f = a*PI*tt*4. + i;

        float s = ssin(f);
        float w = ssin(f*.5+PI*.5);

        float bs = w*.02;               

        float nm = smoothstep(SF+bs, bs, abs(l - (s*.25 + .225))) * w;
    	m = max(m, nm);
    }

    if(iMouse.z > 0.){
      m = 1.-m;	
    }            

    vec3 col = mix(BLACK_COL, WHITE_COL, m);        
    fragColor = vec4(col, 1.);    
}
