void mainImage( out vec4 color, in vec2 coord )
{
	vec4 a = texture(iChannel1,coord/iResolution.xy)*10.;
    color = +.33+.33*cos(6.28*(.4+a.x+vec4(.0,.05,.1,.0)))
        	+.33+.33*cos(6.28*(.4+a.y+vec4(.0,.05,.1,.0)))
        	+.33+.33*cos(6.28*(.4+a.z+vec4(.0,.05,.1,.0)));
}

#define LOOKUP(COORD) texture(iChannel0,COORD/iResolution.xy)
vec4 Field(vec2 position)
{
    vec2 velocityGuess = LOOKUP(position).xy;
    vec2 positionGuess = position - velocityGuess;
	return LOOKUP(positionGuess);
}
void mainImage( out vec4 Energy, in vec2 Me )
{
    vec4 C = Field(Me + vec2(0,0));//CENTER
    vec4 R = Field(Me + vec2(1,0));//RIGHT
    vec4 U = Field(Me + vec2(0,1));//UP
    vec4 L = Field(Me - vec2(1,0));//LEFT
    vec4 D = Field(Me - vec2(0,1));//DOWN

    C.xy += (+vec2(1,0)*(R.z-C.z)
             +vec2(0,1)*(U.z-C.z)
             -vec2(1,0)*(L.z-C.z)
             -vec2(0,1)*(D.z-C.z))/4.;

    C.z = (C.z + R.z + U.z + L.z + D.z)/5.01 +
          (+dot(-vec2(1,0),R.xy-C.xy)
           +dot(-vec2(0,1),U.xy-C.xy)
           +dot(+vec2(1,0),L.xy-C.xy)
           +dot(+vec2(0,1),D.xy-C.xy))/4.;

    if(iFrame == 0 || iMouse.z > 0. && length(Me-iMouse.xy) < 20.)
    {
        C = fract(sin(vec4(dot(Me,vec2(23.123,87.987)),
                           dot(Me,vec2(34.234,96.876)),
                           dot(Me,vec2(45.345,15.765)),
                           dot(Me,vec2(56.456,24.654))))*45678.7654)*2.-1.;
        C*=.1;
    }
    Energy = C;
}

