//draw line and mask
vec2 line(float w, float h, vec2 uv,float pixelsize)
	{
		float linebase =uv.y-h;
		float line = smoothstep(0.0,pixelsize,abs(linebase)-w);
		float linemask = clamp(smoothstep(0.0,pixelsize,linebase-w),0.0,1.0);
		return vec2(line,linemask);
	}

//noise
float weight(float offset,vec2 uv)
	{
		float mask = mix(0.05,1.0,pow(1.0-abs(uv.x-.5)*2.0,3.0));
		return texture(iChannel0, vec2(uv.x*0.1-offset,offset)-iTime*vec2(0.0,0.005)).x*mask;
	}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec2 uv = fragCoord/iResolution.xy;
float image = 0.0;
float linewidth = 1.0;
float pixelsize = 1.0/iResolution.y;
float reallinewidth = linewidth*pixelsize/2.0;

for(int i = 0;i<60;i++)

	{
		float noisesample = weight(float(i)*.5423,uv);


		//draw current line
		vec2 a = line(reallinewidth,0.87-float(i)/10.0/8.0,uv-(noisesample*.2),pixelsize*1.3);  
		a = 1.0-a;

		image = mix(image,a.x,a.y);

	}


    // Output to screen
    fragColor = vec4(image,image,image,1.0);
}
