float PI = 3.14159265;
mat2 genRot(float val){
	return mat2(cos(val),-sin(val),sin(val),cos(val));
}
vec3 modc(vec3 p,vec3 b){
	p = (fract(p / b + 0.5)-0.5) * b;
    return p;
}
vec2 pmod(vec2 p,float c){
	p *= genRot(PI/c);
    float at = atan(p.y/p.x);
    at = mod(at,PI * 2./c);
    float r = length(p);
    p = vec2(cos(at) * r,sin(at) * r);
    p *= genRot(-PI/c);
    return p;
}

float map(vec3 p){
    p.xy *= genRot(-iTime);
    p = modc(p,vec3(4.));
    p.xy *= genRot(iTime * 2.);
    p.xy = pmod(p.xy,8.);
	float sp = length(p - vec3(1.0,0.,0.)) - 0.5;
    float pil = length(p.xy - vec2(2.0,0.)) - 0.1; 
    return min(sp,pil);
}

vec3 getNormal(vec3 p){
	vec3 x = dFdx(p);
    vec3 y = dFdy(p);
    return normalize(cross(x,y));
}

vec4 trace(vec3 o,vec3 r){
	float t = 0.0;
    for(int i = 0; i < 128; i++){
    	vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.25;
    }
    vec3 n = getNormal(o + r * t);
    vec4 result = vec4(n,t);
    return result;

}

vec3 ray(vec2 uv,float a){
	vec3 r = normalize(vec3(uv,a));
    return r;
}

vec3 cam(){
	vec3 c = vec3(0.,0.,-2.5 + iTime);
    return c;
}

vec3 getColor(vec3 o,vec3 r,vec4 data){
	float t = data.w;
    vec3 n = data.xyz;
    float fog = 1./(1. + t * t * 0.025);
    vec3 bc = vec3(1. - dot(r,n));
    float at = atan(r.y/r.x) * 2.;
    vec3 colA;
    colA.r = cos(at +iTime);
	colA.g = cos(at +iTime + PI * 2. / 3.);
    colA.b = cos(at +iTime + PI * 4. / 3.);
	bc *= colA * 0.5 + 0.5;
    return mix(bc,vec3(0.),1. - fog);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * 2. - iResolution.xy) / iResolution.x;
    vec3 o = cam();
    vec3 r = ray(uv,sin(iTime/2.) * 1.5);
    vec4 data = trace(o,r);
    vec3 col = getColor(o,r,data);
    fragColor = vec4(col,0.);
}
