void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float aspect = iResolution.y/iResolution.x;
    float radius = 0.2;
    float factor = 0.1;

    vec2 uv_center = uv - 0.5;
    uv_center.y *= aspect;

    float len = length(uv_center);

    if (len > radius)
    {
        float extra_radial = len - radius;
        uv_center = (uv_center / length (uv_center)) * (radius + factor*extra_radial);
    }

    uv_center = clamp(uv_center, -0.5, 0.5);

    vec2 uv2 = vec2(uv_center.x, uv_center.y / aspect) + 0.5;

    vec4 tt = texture(iChannel0, uv2);



    // Output to screen
    fragColor = tt;
}
